import React, { useState } from "react";

interface AdminPanelProps {
  setPreciosTela: (precios: { [key: string]: number }) => void;
  setPreciosSistema: (precios: { [key: string]: number }) => void;
}

const AdminPanel: React.FC<AdminPanelProps> = ({ setPreciosTela, setPreciosSistema }) => {
  const [tela, setTela] = useState({ Blackout: "", Screen: "", Tradicional: "" });
  const [sistema, setSistema] = useState({ Manual: "", Motorizado: "" });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Solo guarda si todos los campos tienen valor
    if (Object.values(tela).every(v => v) && Object.values(sistema).every(v => v)) {
      setPreciosTela({
        Blackout: Number(tela.Blackout),
        Screen: Number(tela.Screen),
        Tradicional: Number(tela.Tradicional),
      });
      setPreciosSistema({
        Manual: Number(sistema.Manual),
        Motorizado: Number(sistema.Motorizado),
      });
      alert("¡Precios guardados!");
    } else {
      alert("Completa todos los campos antes de guardar.");
    }
  };

  return (
    <div>
      <h2>Panel de Administración</h2>
      <form onSubmit={handleSubmit}>
        <h3>Precios de Telas ($ por m²)</h3>
        <label>
          Blackout:
          <input type="number" value={tela.Blackout} onChange={e => setTela({ ...tela, Blackout: e.target.value })} required />
        </label>
        <label>
          Screen:
          <input type="number" value={tela.Screen} onChange={e => setTela({ ...tela, Screen: e.target.value })} required />
        </label>
        <label>
          Tradicional:
          <input type="number" value={tela.Tradicional} onChange={e => setTela({ ...tela, Tradicional: e.target.value })} required />
        </label>
        <h3>Precios de Sistemas ($ por m²)</h3>
        <label>
          Manual:
          <input type="number" value={sistema.Manual} onChange={e => setSistema({ ...sistema, Manual: e.target.value })} required />
        </label>
        <label>
          Motorizado:
          <input type="number" value={sistema.Motorizado} onChange={e => setSistema({ ...sistema, Motorizado: e.target.value })} required />
        </label>
        <button type="submit" style={{ marginTop: 16 }}>Guardar precios</button>
      </form>
    </div>
  );
};

export default AdminPanel;
